package molBiol;import java.io.Serializable;import molGenExp.HistListItem;import biochem.FoldedPolypeptide;//the object returned by the expressGene method of Genex//it contains the HTML for displaying the gene//as well as info that the selection logic needs in order//to determine which base in which strand was selectedpublic class ExpressedGene extends HistListItem implements Serializable {		public FoldedPolypeptide fp;	public String htmlString;	public String currentDNA;	public Gene theGene;		public ExpressedGene(String HTMLinput, Gene gene) {  // constructor		fp = null;		theGene = gene;		htmlString = HTMLinput;		currentDNA = "";		toolTipText = gene.getProteinString();	}		public String getHTML() {		return htmlString;	}		public Gene getGene() {		return theGene;	}		public void setFoldedPolypeptide(FoldedPolypeptide fp) {		this.fp = fp;	}		public FoldedPolypeptide getFoldedPolypeptide() {		return fp;	}	public String getToolTipText() {		return toolTipText;	}	public void setToolTipText(String text) {		toolTipText = text;	}}