package molBiol;//the object returned by the expressGene method of Genex// it contains the HTML for displaying the gene// as well as info that the selection logic needs in order// to determine which base in which strand was selectedpublic class VisibleGene {    public String htmlString;    public String currentDNA;    public Gene theGene;        VisibleGene(String HTMLinput, Gene gene) {  // constructor        theGene = gene;        htmlString = HTMLinput;        currentDNA = "";    }    public String getHTML() {        return htmlString;    }            public Gene getGene() {        return theGene;    }    }